import React, { useState, useEffect } from 'react';
import EventForm from '../components/EventForm';
import api from '../services/api';


const AdminPanel = () => {
  const [events, setEvents] = useState([]);
  const [eventToEdit, setEventToEdit] = useState(null);

  const fetchEvents = async () => {
    const res = await api.get('/events');
    setEvents(res.data);
  };

  useEffect(() => {
    fetchEvents();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir este evento?')) {
      try {
        await api.delete(`/events/${id}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        fetchEvents();
        if (eventToEdit?._id === id) setEventToEdit(null); // cancela edição se o evento for excluído
      } catch (err) {
        alert('Erro ao excluir evento');
      }
    }
  };

  const handleEdit = (event) => {
    setEventToEdit(event);
  };

  const handleCancelEdit = () => {
    setEventToEdit(null);
  };

  return (
    <div>
      <h2>Painel Administrativo</h2>
      <EventForm onSuccess={() => { fetchEvents(); setEventToEdit(null); }} eventToEdit={eventToEdit} onCancel={handleCancelEdit} />
      <hr />
      <h3>Eventos Cadastrados</h3>
      <ul>
        {events.map(event => (
          <li key={event._id}>
            {event.title} - {new Date(event.date).toLocaleString()}
            <button onClick={() => handleEdit(event)} style={{ marginLeft: '10px' }}>
              Editar
            </button>
            <button onClick={() => handleDelete(event._id)} style={{ marginLeft: '10px' }}>
              Excluir
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};



export default AdminPanel;
